import { Component, OnInit } from '@angular/core';
import {KnightService} from 'src/app/services/knight.service';
import  {Knight} from 'src/app/knight';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})

export class SearchComponent implements OnInit {

  knightList!: Knight[];

  constructor(private knightService : KnightService) { }

  ngOnInit(): void {
    console.log('passou aqui');
    this.getAll();

  }

  async getAll() {
    this.knightService.getAll().subscribe((response) => {
      this.knightList = response;
      console.debug(response);
    });
  }
 }
