import { Component, OnInit } from '@angular/core';
import {
  FormGroup,
  FormControl,
  Validators,
} from '@angular/forms';
import  {Knight} from 'src/app/knight';
import { KnightService} from 'src/app/services/knight.service'

@Component({
  selector: 'app-new',
  templateUrl: './new.component.html',
  styleUrls: ['./new.component.css']
})
export class NewComponent implements OnInit {

  newForm!: FormGroup;
  knight! : Knight;

  constructor(private knightService: KnightService) {}

  ngOnInit() : void {

    this.newForm = new FormGroup({
      name: new FormControl(this.knight.name, [Validators.required]),
      nickname: new FormControl(this.knight.nickName, [Validators.required]),
      birthday: new FormControl(this.knight.birthday, [Validators.required]),
      keyAttribute: new FormControl(this.knight.attribute, [Validators.required]),
      strength: new FormControl(this.knight.strength, [Validators.required]),
      dexterity: new FormControl(this.knight.dexterity, [Validators.required]),
      constitution: new FormControl(this.knight.constitution, [Validators.required]),
      intelligence: new FormControl(this.knight.intelligence, [Validators.required]),
      charisma: new FormControl(this.knight.charisma, [Validators.required]),
      wisdom: new FormControl(this.knight.wisdom, [Validators.required]),
    });

  }

  submit(){

    if(this.newForm.invalid)
      return;


      this.knightService.create(this.knight);
  }
}
